const chartDataCombined = {
  labels: timeStamps,
  datasets: [
    {
      label: "Latence (ms)",
      data: latencyData,
      borderColor: "rgba(255, 105, 180, 1)", // Pink color for latency curve
      backgroundColor: "rgba(255, 105, 180, 0.2)", // Light pink background
      fill: true,
      tension: 0.4,
      pointRadius: 5,
      pointBackgroundColor: 'rgba(255, 105, 180, 1)',
      borderWidth: 3,
      pointHoverRadius: 8,
      yAxisID: 'y1', // Use the first y-axis for latency
    },
    {
      label: "Débit (Mbps)",
      data: bandwidthData,
      borderColor: "rgba(0, 0, 128, 1)", // Navy blue for bandwidth curve
      backgroundColor: "rgba(0, 0, 128, 0.2)", // Light blue background
      fill: true,
      tension: 0.4,
      pointRadius: 5,
      pointBackgroundColor: 'rgba(0, 0, 128, 1)',
      borderWidth: 2,
      pointHoverRadius: 8,
      yAxisID: 'y2', // Use the second y-axis for bandwidth
    },
  ],
};

const chartOptionsCombined = {
  responsive: true,
  maintainAspectRatio: false,
  plugins: {
    legend: {
      display: true,
    },
    tooltip: {
      enabled: true,
    },
  },
  scales: {
    x: {
      title: {
        display: true,
        text: "Temps",
      },
      grid: {
        color: '#e1e1e1', // Color for grid lines
      },
    },
    y1: {
      title: {
        display: true,
        text: "Latence (ms)",
      },
      grid: {
        color: '#e1e1e1', // Color for grid lines
      },
      position: 'left', // Place the first y-axis on the left
    },
    y2: {
      title: {
        display: true,
        text: "Débit (Mbps)",
      },
      grid: {
        color: '#e1e1e1', // Color for grid lines
      },
      position: 'right', // Place the second y-axis on the right
    },
  },
};

// In your render function, you would use `chartDataCombined` and `chartOptionsCombined` for the Line chart.
